import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const ProductList = () => {
  const [pegawais, setProducts] = useState([]);

  useEffect(() => {
    getProducts();
  }, []);

  const getProducts = async () => {
    const response = await axios.get("http://localhost:5000/pegawai");
    setProducts(response.data);
  };

  const deleteProduct = async (productId) => {
    try {
      await axios.delete(`http://localhost:5000/pegawai/${productId}`);
      getProducts();
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className="columns mt-5 is-centered">
        <div className="column is-half">
            <Link to={`add`} className='button is-success'>Add New</Link>
            <table className='table is-striped is-fullwidth'>
                <thead>
                    <tr>
                        <th>No</th>
                        <th>Name</th>
                        <th>Product</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {products.map((product, index) => (
                <tr key={product.id}>
                    <td>{index + 1}</td>
                    <td>{product.name}</td>
                    <td><img src={product.url} alt="Image" width={80} height={40}/></td>
                    <td>
                        <Link to={`edit/${product.id}`} className='button is-small is-info'>Edit</Link>
                        <button onClick={() => deleteProduct(product.id)} className='button is-small is-danger'>Delete</button>
                    </td>
                </tr>
                    ))}
                </tbody>
            </table>
        </div>
    </div>
  );
};

export default ProductList;
